    void KMPSearch(char* pat, char* txt) {
    	int M = strlen(pat);
    	int N = strlen(txt);

    	int lps[M];

    	computeLPSArray(pat, M, lps);

    	int i = 0, j = 0;
    	while (i < N) {
    		if (pat[j] == txt[i]) {
    			j++;
    			i++;
    		}
    		if (j == M) {
    			cout << "Found Pattern at index " << (i - j) << endl;
    			j = lps[j - 1];
    		}
    		// íŒ¨í„´ì´ ì¼ì¹˜í•˜ì§€ì•Šìœ¼ë©´
    		else if (i < N && pat[j] != txt[i]) {
    			// ë¯¸ìŠ¤ë§¤ì¹­ì´ 1~Mì´ë©´
    			if (j != 0)
    				j = lps[j - 1];
    			else // ì²« ë¬¸ìë¶€í„° ë¯¸ìŠ¤ë§¤ì¹­
    				i = i + 1;
    		}
    	}
    }
